const queue = []
const transitionDuration = 300

function createToastNode(toast) {
  const node = document.createElement("div")

  node.classList.add("mds-notification")
  node.innerText = toast.text

  Object.assign(node.style, {
    alignItems: "center",
    backgroundColor: "#282e32",
    borderRadius: "0.5rem",
    bottom: "-2.5rem",
    boxShadow: "0 0 0.5rem rgba(0 0, 0, 0.2)",
    boxSizing: "border-box",
    color: "#b6c2cf",
    display: "flex",
    height: "2.5rem",
    left: "50%",
    margin: "0 auto",
    padding: "0.5rem 1.5rem",
    position: "fixed",
    transform: "translateX(-50%) scaleX(0)",
    transition: `all ${transitionDuration}ms ease-in-out`,
    zIndex: 99999,
  })

  return node
}

let drainId
function showToast(toast) {
  const node = createToastNode(toast)
  document.body.appendChild(node)

  // Wait for the next frame to animate in the notification
  window.requestAnimationFrame(() => {
    node.style.bottom = "1rem"
    node.style.transform = "translateX(-50%) scaleX(1)"
  })

  // Wait 3 seconds before animating out the notification
  clearTimeout(drainId)
  drainId = window.setTimeout(drainQueue, toast.duration)
}

function runAfter(ms, callback) {
  if (ms) window.setTimeout(callback, ms)
  else callback()
}

function drainQueue() {
  const notifications = document.querySelectorAll(".mds-notification")

  // Animate out all notifications
  notifications.forEach((node) => {
    node.style.opacity = 0
  })

  // Wait for the animation to finish before removing the notification
  runAfter(notifications.length ? transitionDuration : 0, () => {
    notifications.forEach((node) => {
      document.body.removeChild(node)
    })

    // Show the next notification in the queue if there is one
    if (queue.length) {
      showToast(queue.shift())
    }
  })
}

/**
 * Notify the user of an action.
 * @param {string} text - The text to display in the notification.
 * @param {number} duration - The duration of the notification in milliseconds.
 */
function notify(text, duration = 3000) {
  queue.push({ text, duration })
  drainQueue()
}

/**
 * Convert a string to a slug.
 * @param {string} str - The string to convert.
 */
function slugify(str) {
  return str
    .toLowerCase()
    .replace(/['"]/g, "")
    .replace(/[^a-z0-9]+/g, "-")
    .replace(/-{2,}/g, "-")
    .trim()
}

/**
 * Get a selector for a test ID.
 * @param {string} id - The test ID to select.
 */
const testId = (id) => `[data-testid="${id}"]`

const selectors = {
  assignee: testId("issue.views.field.user.assignee"),
  issueNum: testId(
    "issue.views.issue-base.foundation.breadcrumbs.current-issue.item",
  ),
  issueTitle: testId("issue.views.issue-base.foundation.summary.heading"),
  toolbar: testId("ak-editor-main-toolbar"),
}

window.addEventListener("load", () => {
  function getIssueNumber() {
    return document.querySelector(selectors.issueNum)?.textContent
  }

  function getBranchName() {
    const num = getIssueNumber()
    const title = document
      .querySelector(selectors.issueTitle)
      ?.textContent.replace(/^(fe|frontend|front end|front-end)/i, "")

    const slug = slugify(title)
      .split("-")
      .slice(0, 7) // Limit to the first 7 words
      .join("-")

    return `${num.toLowerCase()}-${slug}`
  }

  // Quickly copy command to create a new branch when pressing cmd+shift+g
  document.body.addEventListener("keydown", async (e) => {
    if (!(e.metaKey && e.shiftKey)) return

    switch (e.key) {
      // Copy new branch command
      case "g":
        e.preventDefault()
        await navigator.clipboard.writeText(`git new ${getBranchName()}`)
        notify("Copied new branch command")
        break

      // Copy rename branch command
      case "e":
        e.preventDefault()
        await navigator.clipboard.writeText(`git name ${getBranchName()}`)
        notify("Copied rename branch command")
        break

      // Copy worktree link
      case "p":
        e.preventDefault()
        await navigator.clipboard.writeText(
          `git worktree add ../${getBranchName()}`,
        )
        notify("Copied add worktree command")
        break

      // Assign to me
      case "d": {
        e.preventDefault()
        const field = document.querySelector(selectors.assignee)
        const button = field?.querySelector('[role="button"]')

        if (button?.innerText === "Assign to me") {
          button.click()
          notify("Issue assigned to you")
        }
      }
    }
  })

  function focusElement(nodes, getNextIdx) {
    if (!document.activeElement) return

    const index = nodes.indexOf(document.activeElement)
    const next = nodes[getNextIdx(index)]

    next.tabIndex = 0
    next.focus()
    nodes[index].tabIndex = -1
  }

  // Fix keyboard navigation for the RTE toolbar
  document.body.addEventListener("keyup", (e) => {
    const toolbar = document.activeElement?.closest(selectors.toolbar)
    const actions = [...(toolbar?.querySelectorAll("button").values() ?? [])]

    switch (e.key) {
      case "Tab":
        actions
          .filter((node) => node !== document.activeElement)
          .forEach((node) => node.setAttribute("tabindex", -1))
        break

      case "ArrowLeft":
        focusElement(actions, (i) => Math.max(i - 1, 0))
        break

      case "ArrowRight":
        focusElement(actions, (i) => Math.min(i + 1, actions.length - 1))
        break
    }
  })
})

